'use strict';

// const util = require('util');
const delegate = require('./3rd-party/delegates');
// const Cookies = require('./cookies');

// const COOKIES = Symbol('context#cookies');

const proto = module.exports = {
  // get cookies() {
  //     if (!this[COOKIES]) {
  //         this[COOKIES] = new Cookies(this.req, this.res)
  //     }
  //     return this[COOKIES]
  // },

  // get cookies(cookies) {
  //     this[COOKIES] = cookies
  // }
}

/**
 * Response delegation.
 */

delegate(proto, 'response')
  .method('attachment')
  .method('redirect')
  .method('remove')
  .method('vary')
  .method('set')
  .method('append')
  .method('flushHeaders')
  .access('status')
  .access('message')
  .access('body')
  .access('length')
  .access('type')
  .access('lastModified')
  .access('etag')
  .getter('headerSent')
  .getter('writable');

/**
 * Request delegation.
 */

delegate(proto, 'request')
  .method('acceptsLanguages')
  .method('acceptsEncodings')
  .method('acceptsCharsets')
  .method('accepts')
  .method('get')
  .method('is')
  .access('querystring')
  .access('idempotent')
  .access('socket')
  .access('search')
  .access('method')
  .access('query')
  .access('path')
  .access('url')
  .access('accept')
  .getter('origin')
  .getter('href')
  .getter('subdomains')
  .getter('protocol')
  .getter('host')
  .getter('hostname')
  .getter('URL')
  .getter('header')
  .getter('headers')
  .getter('secure')
  .getter('stale')
  .getter('fresh')
  .getter('ips')
  .getter('charset')
  .getter('ip');